    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        Log.w(MySQLiteHelper.class.getName(),
                "Upgrading database from version " + oldVersion + " to "
                        + newVersion + ", which will destroy all old data");
        if (oldVersion == 87) {
            db.execSQL("update tblCards set NumFoilsOwned=(select NumFoilsOwned from tblBackup where _id=214) where _id=214");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumFoilsOwned from tblBackup where _id=1339) where _id=1339");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumFoilsOwned from tblBackup where _id=952) where _id=952");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumFoilsOwned from tblBackup where _id=93) where _id=93");
        }
        db.execSQL("DROP TABLE IF EXISTS tblBackup");
        db.execSQL(DATABASE_BACKUP_CREATE);
        //Add foils owned column if it didn't exist in the old version
        if (oldVersion <= 84){
            db.execSQL("ALTER TABLE tblCards ADD COLUMN NumFoilsOwned tinyint not null DEFAULT -1");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumOwned from tblCards where _id=1684) where _id=214");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumOwned from tblCards where _id=1685) where _id=1339");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumOwned from tblCards where _id=1686) where _id=952");
            db.execSQL("update tblCards set NumFoilsOwned=(select NumOwned from tblCards where _id=1687) where _id=93");
            db.execSQL("delete from tblCards where _id in(1684,1685,1686,1687)");
        }
        db.execSQL(DATABASE_BACKUP_POPULATE);
        // delete this once everyone is upgraded to version 1.1
        db.execSQL(DATABASE_TEAMS_CREATE);
        // end delete
        db.execSQL(DATABASE_TEMPTEAMS_CREATE);
        db.execSQL(DATABASE_TEMPTEAMS_POPULATE);
        db.execSQL("DROP TABLE IF EXISTS tblCards");
        db.execSQL("DROP TABLE IF EXISTS tblSettings");
        db.execSQL("DROP TABLE IF EXISTS tblTeams");
        db.execSQL("DROP TABLE IF EXISTS tblPlayerStats");
        onCreate(db);
        if (oldVersion <= 84) {
            db.execSQL("update tblCards set DiceOwned=(select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName), NumOwned=(select NumOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName) where exists (select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName)");
        } else {
            //db.execSQL("update tblCards set DiceOwned=(select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName), NumOwned=(select NumOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName), NumFoilsOwned=(select NumFoilsOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName) where exists (select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName)");
            db.execSQL("update tblCards set DiceOwned=(select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName), NumOwned=(select NumOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName) where exists (select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName)");
            db.execSQL("update tblCards set NumFoilsOwned=(select case when NumFoilsOwned<0 then 0 else NumFoilsOwned end from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName) where NumFoilsOwned>=0 and exists (select DiceOwned from tblBackup where CardSet=tblCards.CardSet and CharName=tblCards.CharName and CardName=tblCards.CardName)");
        }
        db.execSQL("insert into tblTeams select TeamName, CardSet, CharName, CardName, NumDice from tblTempTeams");
        db.execSQL("DROP TABLE IF EXISTS tblTempTeams");
    }
